import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

interface Lecturer {
  id: number;
  lid: string;
  name: string;
  taxBand: string;
  salaryScale: number;
}

@Component({
  selector: 'app-lecturers',
  templateUrl: './lecturers.component.html',
  styleUrls: ['./lecturers.component.css']
})
export class LecturersComponent {
  constructor(private http: HttpClient) { }

  lecturers: Lecturer[] = [];

  ngOnInit() {
    this.http.get<Lecturer[]>('http://localhost:8080/lecturers').subscribe(data => {
      this.lecturers = data;
    });
  }

  trackById(index: number, lecturer: Lecturer): number {
    return lecturer.id;
  }
}
