import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-update-lecturer',
  templateUrl: './update-lecturer.component.html',
  styleUrls: ['./update-lecturer.component.css']
})
export class UpdateLecturerComponent implements OnInit {
  lid: string = ''; // Initialize lid to an empty string

  constructor(private route: ActivatedRoute) {}

  ngOnInit(): void {
    const lidParam = this.route.snapshot.paramMap.get('lid');
    if (lidParam !== null) {
      this.lid = lidParam;
    } else {
      // Handle the case where the lid is null or undefined
    }
  }
}
<h3>Update Lecturer</h3>

<form (submit)="onSubmit($event)">
    LID: <input type="text" name="lid" [value]="lid" disabled><br>
    <input type="hidden" name="lid" [value]="lid">
    Name: <input type="text" name="name"> <br>
    Tax Band: <input type="text" name="taxBand"> <br>
    Salary Scale: <input type="number" name="salaryScale"> <br>
    <br>
    <button type="submit">Submit</button>
</form>
