import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { LecturerService } from '../services/lecturer.service';

@Component({
  selector: 'app-update-lecturer',
  templateUrl: './update-lecturer.component.html',
  styleUrls: ['./update-lecturer.component.css']
})
export class UpdateLecturerComponent implements OnInit {
  lid: string = ''; // Initialize lid to an empty string
  name: string = '';
  taxBand: string = '';
  salaryScale: number = 0;

  constructor(
    private route: ActivatedRoute,
    private lecturerService: LecturerService
  ) {}

  ngOnInit(): void {
    const lidParam = this.route.snapshot.paramMap.get('lid');
    if (lidParam !== null) {
      this.lid = lidParam;
    } else {
      // Handle the case where the lid is null or undefined
    }
  }

  onSubmit(): void {
    this.lecturerService.updateLecturer(
      this.lid,
      this.name,
      this.taxBand,
      this.salaryScale
    ).subscribe(result => {
      console.log(result);
      // Handle the response here
    }, error => {
      console.log(error);
      // Handle the error here
    });
  }
}
